(237, 'document', 'text/html', 'Для взрослых', 'Сценарии сюжетно-ролевых игр для взрослых Questoria', '', 'для-взрослых', '', 1, 0, 0, 47, 0, '', '<h1>Сценарии игр</h1>\r\n[!Scenarios? &amp;show_all_links=1 &amp;show_not_available_scenarios=1 &amp;button_text=Оставить заявку &amp;show_more_text=0 &amp;selected_tag=adult !]\r\n<h2><a href="/квестория/сценаристам-квестов">Хотите писать сценарии игр для Квестории?</a> </h2>', 0, 8, 300, 1, 1, 11, 1415040248, 11, 1452003824, 0, 0, 0, 1322731849, 1, '', 0, 0, 0, 0, 0, 0, 1);
